id_t id;
clock t;

void reset_b()
{
    for(i : id_t)
        b[id][i] = true;
}

nta.system:
P0 = Process();
P1 = Process();
P2 = Process();

C0 = Channel();
C1 = Channel();
C2 = Channel();

//O = Observer(500);
H = Helper();

system P0, P1, P2, C0, C1, C2, H;
